  def self.enriched_titles(title_cont: nil, **options)
    BOOKS.map do |titel, reeks, deel, info, bouw|
      next unless titel.present?
      next if title_cont.present? && !titel.upcase[title_cont.upcase]
      enrichment = [
        info.present? ? info           : nil,
        bouw.present? ? bouw           : nil,
        deel.present? ? "deel #{deel}" : nil,
      ].compact.join(' | ')
      "#{titel.capitalize} [[#{enrichment}]]"
    end.compact.sort.uniq
  end

  def self.series(series_cont: nil, **options)
    BOOKS.map do |_titel, reeks, _deel, _info, _bouw|
      next unless reeks.present?
      next if series_cont.present? && !reeks.upcase[series_cont.upcase]
      reeks.capitalize
    end.compact.sort.uniq
  end

